// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;


contract EtherPortal {

    //event PaymentsMade(address payable[] recipients, uint[] amounts);

    // batch push payment method that is immune to reentrancy, unpredictable gas costs, and/or revert() triggering from recipient's fallback()
    // see https://fravoll.github.io/solidity-patterns/pull_over_push.html for motivation
    // due to selfdestruct opcode implementation, recipients cannot react to ether sent for better or worse

    // gas cost is cheaper than direct transaction from EOA (21k gas) when making >10 payments

    // solc 0.7.6
    // 1486350 gas cost to batch pay 100 existing account addresses, ~ 30% cheaper over direct EOA transfer, 
    // gas cost ~= 73k + 14k*(total number of recipients) + 25k*(number of recipients without pre-existing state objects)

    // solc 0.8.4
    // 1607506 gas cost to batch pay 100 existing account addresses 
    // gas cost ~= 91k + 15k*(total number of recipients) + 25k*(number of recipients without pre-existing state objects) 

    // solc 0.6.12
    // 1486327 gas cost to batch pay 100 existing account addresses 

    // ropsten solc 0.8.4
    // 225,139 gas cost for 10 payments

    
    // msg.value must be >= sum(_ammounts) or transaction will revert
    function massTeleport(address payable[] memory _recipients, uint[] memory _amounts) public payable returns (bool) {
      
      uint numRecipients = _recipients.length;
      require(numRecipients == _amounts.length, 'Inconsistent parameter array lengths');

      // open payment portal, note portal only closes at the end of transaction, no matter how many times selfdestruct() is invoked
      WormHole unstablePortal = new WormHole(); 

      for (uint i=0; i < numRecipients; i++) {
        unstablePortal.teleportEtherTo{value: _amounts[i]}(_recipients[i]); // pay recipient by invoking selfdestruct() within the portal
      }

      if (address(this).balance > 0) {
        unstablePortal.teleportEtherTo{value: address(this).balance}(payable(msg.sender)); // return any unused ether to sender
      }

      return true;
    }

    /*
    function batchPayWithEvent(address payable[] memory _recipients, uint[] memory _amounts) public payable returns (bool) { 

      if ( batchPay(_recipients, _amounts) ) {
        emit PaymentsMade(_recipients, _amounts);
        return true;
      }

      return false;

    }
    */


    // function will permanently erase msg.value off the blockchain
    // ether erased in this manner cannot be recovered, even if an entity could effortlessly conjure up private keys to any and all addresses
    // of dubious utility, but perhaps of academic interest for questions such as "what is the total amount of ether on the blockchain" 
    // 77k gas on ropsten to void 0.1 eth
    function voidEther() public payable {

      WormHole unstablePortal = new WormHole();
      unstablePortal.teleportEtherTo{value: msg.value}(payable(address(unstablePortal)));

    }
}




contract WormHole {

  function teleportEtherTo(address payable _recipient) public payable {
    selfdestruct(_recipient);
  }
}
